import { useRef } from 'react';
import './App.css';


function App() {

  const fileInputRef = useRef();

  const logo = "https://img.freepik.com/free-vector/video-content-creator-blogger-colorful-cartoon-character-video-editing-uploading-cutting-arrangement-video-shot-manipulation_335657-762.jpg?w=740&t=st=1713546616~exp=1713547216~hmac=c70e785d0f168e52ffc2b6774247f0f66ed18ae9f5cab51dfe5cd198f72d3dda";

  const onUploadClick = () => {
    fileInputRef.current.click();
  }
  return (
    <div class="container"><img src={logo} alt="banner" />
    <div class="wrapper">
        <h1>Simple File Sharing!</h1>
        <p>Upload and share the download link.</p>

        <button onClick={() => onUploadClick()}>Upload</button>
        <input type="file"
          ref={fileInputRef}
          style
        />
      </div>
    </div>


  );
}

export default App;
